async function hobbiesData(){let res;return(await fetch("data/hobbies.json")).json()}document.addEventListener("DOMContentLoaded",async()=>{let list=document.querySelector("#boxes"),input=document.querySelector("#search");await hobbiesData().then(hobbies=>{hobbies.map((item,ind)=>{let color="#"+(Math.floor(899999*Math.random())+1e5);list.innerHTML+=`<div class="box" style="background: ${color}" id="${ind}"><a href="https://duckduckgo.com/?q=${item.title} hobbie" target="_blank">${item.title}</a></div>`})}),input.addEventListener("keyup",()=>{let filter=input.value.toUpperCase(),box=document.querySelectorAll(".box");if(0==filter.length)for(i=0;i<box.length;i++)box[i].style.display="block";for(i=0;i<box.length;i++)a=box[i].querySelector("a"),txtValue=a.textContent||a.innerText,txtValue.toUpperCase().indexOf(filter)>0?box[i].style.display="block":box[i].style.display="none"})});